mapper.select:
select
    c.id as "id",
    c.member_id as "memberId",
    m.name as "memberName",
    c.post_id as "postId",
    c.content as "content",
    c.created_at as "createdAt"
from comment c join member m on (c.member_id = m.id)
where c.post_id = #{postId}
    and c.id > #{id}
order by c.id asc
limit #{size}

select count(*)
from comment
where post_id = #{postId}
